clear all
clc
%%
function y = smooth_piecewise(x)
    y = zeros(size(x));

    % 段1：直线 y=x, 0<=x<=1
    idx1 = (x <= 1);
    y(idx1) = 1.25;

    % 段2：Hermite 过渡，1<x<=1.5
    idx2 = (x > 1) & (x <= 1.5);
    L  = 0.5;           % 区间长度
    y0 = 1.25;  y1 = 1; % 端点值
    m0 = 0;  m1 = -1;    % 端点斜率 (对 x)
    s = (x(idx2) - 1) / L;

    h00 = 2*s.^3 - 3*s.^2 + 1;
    h10 = s.^3   - 2*s.^2 + s;
    h01 = -2*s.^3 + 3*s.^2;
    h11 = s.^3   - s.^2;

    y(idx2) = h00.*y0 + h10.*(m0*L) + h01.*y1 + h11.*(m1*L);

    % 段3：水平延展，x>1.5
    idx3 = (x > 1.5);
    y(idx3) = x(idx3);
end


x = linspace(0, 2, 400);
y = smooth_piecewise(x);

plot(x, y, 'LineWidth', 2); grid on; xlabel('x'); ylabel('y'); %[output:9fdcf40d]
hold on; plot([0 1],[0 1],'k--'); % 参考直线 %[output:9fdcf40d]



