%%
%[text] 
global params
params = init_params();

F = params.F;
R = params.R_gas;
T = params.T;
Cm = params.Cm;

Nao=params.Nao; % mM
Ko=params.Ko;    % mM
Nai=params.Nai_0; % mM
Ki=params.Ki_0; % mM

P_K_h=params.P_K_h;       
P_Na_h=params.P_Na_h;
%%
%[text] 
Vm_mV=[-100 -90 -80 -70] %[output:259d3b9c]
Vm_V = Vm_mV / 1000 %[output:97f513c2]

Ih_Inf_pA = [-290 -230 -150 -80] %[output:2f17b1fa]

tau_ms = [64 68 84 112] %[output:6e7596e6]
tau_s = tau_ms / 1000 %[output:1933dffa]


zFVRT_K = 1 * F * Vm_V / (R * T);
zFVRT_Na = 1 * F * Vm_V / (R * T);
CF_K = zFVRT_K .* (Ki - Ko * exp(-zFVRT_K)) ./ (1 - exp(-zFVRT_K));
CF_Na = zFVRT_Na .* (Nai - Nao * exp(-zFVRT_Na)) ./ (1 - exp(-zFVRT_Na));

ph_Inf=Ih_Inf_pA./(  Cm*(P_K_h*CF_K + P_Na_h*CF_Na)  );
ph_Inf = ph_Inf / max(abs(ph_Inf)) %假设在-100 mV,ph为1 %[output:3541500c]


%%
%[text] fitting Vhalf和k
%[text] ![](text:image:84f4)
%[text] 
eps0 = 1e-6;
ph_Inf = min(max(ph_Inf, eps0), 1-eps0); % 保护数值：避免 0 或 1

%    ln(1/p - 1) =(1/k)*V + (-Vhalf/k)  y ≈ a*V + b
y  = log(1./ph_Inf - 1);
p  = polyfit(Vm_mV, y, 1);    
a = p(1);  b = p(2);
k    = 1/a %[output:773b4faa]
Vhalf   = -b*k %[output:4fa2ded9]
%[text] 


tau_s_mean = mean(tau_s) %[output:47285f9c]






